import { Parallax } from "react-parallax";

const Hero = ({ bgImage, bgImageAlt, className, h1Content }) => (
  <Parallax
    blur={2}
    bgImage={bgImage}
    bgImageAlt={bgImageAlt}
    strength={800}
    className={className}
  >
    <div>
      <h1 className="hero-heading">
        {h1Content.map((item, index) => (
          <span key={index} className={item.className}>
            {item.text}
          </span>
        ))}
      </h1>
      <p className="hero-paragraph">
        {h1Content.map((item, index) => (
          <span key={index} className={item.className}>
            {item.text}
          </span>
        ))}
      </p>
    </div>
  </Parallax>
);

export default Hero;
{
  /* find your notes in just a few clicks: Keep your campaign details organized and easily accessible.
The ideal companion for all role-players: Whether you're a seasoned Game Master or a new player, Note Maker helps you stay on top of your game. */
}
