function App() {
  // État pour stocker la couleur actuelle du fond
  const [backgroundColor, setBackgroundColor] = React.useState("");

  // Fonction pour changer la couleur du fond en blanc
  const styl = () => {
    setBackgroundColor("white");
  };

  return (
    <div style={{ backgroundColor }}>
      {/* Bouton qui déclenche la fonction styl au clic */}
      <button onClick={styl}>Change mode</button>
    </div>
  );
}

export default App;
